package interviewQuestionsAndAnswers;

public class StarPatternMaxToMin {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i,rc=5,j,starCount=5;
		
		for(i=1;i<=rc;i++){
			
			System.out.println();
			
			
			
			for(j=1;j<=starCount;j++)
			{
				System.out.print("x");
				
			}
			starCount--;
		}
		
		
	}

}
