package java2020;

public class w3break {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		/*
		 * for(int i=0; i <10;i++) { if (i==4) { break; }
		 * 
		 * System.out.println("The value of i is"+i);
		 * 
		 * }
		 */
		
		
		for(int j=0; j< 10;j++)
		{
			
			if(j==7)
			{
				continue;
			}
			
			System.out.println("The value of j is "+j);
		}
	}

}
